import React, { useEffect } from "react";
import Hero from "../Hero/Hero";
import { motion, useTransform, useScroll } from "framer-motion";
import "./Home.css";
function Home() {
  const { scrollYProgress } = useScroll();
  const rightSide = useTransform(scrollYProgress, [1, -0.7, 0], [2000, 0, 0]);
  const leftSide = useTransform(scrollYProgress, [1, -0.7, 0], [-2000, 0, 0]);
  return (
    <div className="home-container">
      <motion.div
        className="homeDiv"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        style={{ x: leftSide }}
      >
        <div className="leftSide">
          <h2>Keep Track Of Your Loved Ones</h2>
          <h1>
            <span className="highlight">MeRing a Life Savior.</span>
          </h1>
          <h3>A health tracker which allows constant vitals monitoring.</h3>
          <button className="readMore">Read More</button>
        </div>
      </motion.div>
      <div className="rightSide">
        <motion.div
          className="homeDiv"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          style={{ x: rightSide }}
        >
          <Hero />
        </motion.div>
      </div>
    </div>
  );
}
export default Home;
