import React from "react";
import "./Showcase.css";
import { Slides } from "./Slides";
import Card from "./Card";
import { AnimatePresence, motion } from "framer-motion";

const Showcase = () => {
  const slides = Slides;
  const [index, setIndex] = React.useState(0);
  const nextHandler = () => {
    if (index < slides.length - 1) {
      setIndex(index + 1);
    } else {
      setIndex(0);
    }
  };
  const prevHandler = () => {
    if (index > 0) {
      setIndex(index - 1);
    } else {
      setIndex(slides.length - 1);
    }
  };
  return (
    <div className="showcaseContainer">
      <button className="nextSlide" onClick={nextHandler}>
        ≻
      </button>
      <button className="prevSlide" onClick={prevHandler}>
        ≺
      </button>
      <AnimatePresence
        // This will be used for components to resolve
        // exit variants. It's necessary as removed
        // components won't rerender with
        // the latest state (as they've been removed)
        custom={direction}
      >
        <motion.div
          key={index}
          className="slide"
          data-page={currentPage}
          variants={variants}
          initial="enter"
          animate="active"
          exit="exit"
          drag="x"
          onDrag={detectPaginationGesture}
          onDragStart={() => (hasPaginated.current = false)}
          onDragEnd={() => (hasPaginated.current = true)}
          // Snap the component back to the center
          // if it hasn't paginated
          dragConstraints={{ left: 0, right: 0, top: 0, bottom: 0 }}
          // This will be used for components to resolve all
          // other variants, in this case initial and animate.
          custom={direction}
        />
        <motion.div
          initial={{ opacity: 0, x: -280 }}
          animate={{ opacity: 1, x: 0, y: 0 }}
          transition={{
            ease: "easeInOut",
            delay: 0,
          }}
        >
          <Card slide={slides[index]} />
        </motion.div>
      </AnimatePresence>
    </div>
  );
};
export default Showcase;
