import React from "react";
import "./Showcase.css";
import { Slides } from "./Slides";
import Card from "./Card";
const Showcase = () => {
  const slides = Slides;
  const [index, setIndex] = React.useState(0);
  const nextHandler = () => {
    if (index < slides.length) {
      setIndex(index + 1);
    } else {
      setIndex(0);
    }
  };
  const prevHandler = () => {
    if (index > 0) {
      setIndex(index - 1);
    } else {
      setIndex(0);
    }
  };
  return (
    <>
      <button
        className="nextSlide"
        onClick={index < slides.length ? setIndex(index + 1) : setIndex(0)}
      >
        Next
      </button>
      <Card slide={slides[index]} />
      <button
        className="prevSlide"
        onClick={index > 0 ? setIndex(index + 1) : setIndex(0)}
      >
        Previous
      </button>
    </>
  );
};
export default Showcase;
