import React, { useEffect } from "react";
import "./Showcase.css";
import { Slides } from "./Slides";
import Card from "./Card";
import Particles from "react-tsparticles";
import { polygonPathName, loadPolygonPath } from "tsparticles-path-polygon";
const options: ISourceOptions = {
  background: {
    color: "#0d47a1",
  },
  interactivity: {
    events: {
      onClick: {
        enable: true,
        mode: "push",
      },
      onHover: {
        enable: true,
        mode: "repulse",
      },
      resize: true,
    },
    modes: {
      bubble: {
        distance: 400,
        duration: 2,
        opacity: 0.8,
        size: 40,
      },
      push: {
        quantity: 4,
      },
      repulse: {
        distance: 200,
        duration: 0.4,
      },
    },
  },
  particles: {
    color: {
      value: "#ffffff",
    },
    links: {
      color: "#ffffff",
      distance: 150,
      enable: true,
      opacity: 0.5,
      width: 1,
    },
    collisions: {
      enable: true,
    },
    move: {
      direction: "none",
      enable: true,
      outMode: "bounce",
      random: false,
      speed: 6,
      straight: false,
    },
    number: {
      density: {
        enable: true,
        value_area: 800,
      },
      value: 80,
    },
    opacity: {
      value: 0.5,
    },
    shape: {
      type: "circle",
    },
    size: {
      random: true,
      value: 5,
    },
  },
};
const Showcase = () => {
  const slides = Slides;
  const [index, setIndex] = React.useState(0);
  const slidesLength = slides.length;
  const nextHandler = () => {
    if (index < slides.length - 1) {
      setIndex(index + 1);
    } else {
      setIndex(0);
    }
  };

  const prevHandler = () => {
    if (index > 0) {
      setIndex(index - 1);
    } else {
      setIndex(slides.length - 1);
    }
  };

  return (
    <div className="showcaseContainer">
      <button className="nextSlide" onClick={nextHandler}>
        ≻
      </button>
      <button className="prevSlide" onClick={prevHandler}>
        ≺
      </button>
      <div className="slidesContainer">
        {index - 1 > 0 ? (
          <Card slide={slides[index - 1]} className="leftSlide" />
        ) : (
          <Card slide={slides[slidesLength - 1]} className="leftSlide" />
        )}

        <Card slide={slides[index]} className="centerSlide" />

        {index + 1 < slidesLength ? (
          <Card slide={slides[index + 1]} className="leftSlide" />
        ) : (
          <Card slide={slides[0]} className="leftSlide" />
        )}
      </div>
      <Particles
        options={{
          fpsLimit: 60,
          particles: {
            color: {
              value: "#FF0000",
              animation: {
                enable: true,
                speed: 10,
              },
            },
            move: {
              attract: {
                enable: true,
                rotate: {
                  distance: 100,
                  x: 2000,
                  y: 2000,
                },
              },
              direction: "none",
              enable: true,
              outModes: {
                default: "destroy",
              },
              path: {
                clamp: false,
                enable: true,
                delay: {
                  value: 0,
                },
                generator: polygonPathName,
                options: {
                  sides: 6,
                  turnSteps: 30,
                  angle: 30,
                },
              },
              random: false,
              speed: 3,
              straight: false,
              trail: {
                fillColor: "#000",
                length: 20,
                enable: true,
              },
            },
            number: {
              density: {
                enable: true,
                area: 800,
              },
              value: 0,
            },
            opacity: {
              value: 1,
            },
            shape: {
              type: "circle",
            },
            size: {
              value: 2,
            },
          },
          background: {
            color: "#000",
          },
          fullScreen: {
            zIndex: -1,
          },
          detectRetina: true,
          emitters: {
            direction: "none",
            rate: {
              quantity: 1,
              delay: 0.25,
            },
            size: {
              width: 0,
              height: 0,
            },
            position: {
              x: 50,
              y: 50,
            },
          },
        }}
        init={particlesInit}
      />
    </div>
  );
};
export default Showcase;
