import React from "react";
import { Slides } from "../ImageSlider/Slides";
import one from "./1.jpeg";
const Test = () => {
  const slides = Slides;
  const [currSlide, setSlide] = React.useState(0);
  const nextSlide = () => {
    setSlide(currSlide + 1);
  };
  const prevSlide = () => {
    setSlide(currSlide - 1);
  };
  const prevImage = () => {
    if (currSlide - 1 < 0) {
      return slides[slides.length - 1].img;
    }
    return slides[currSlide - 1].img;
  };
  const nextImage = () => {
    if (currSlide + 1 > slides.length) {
      return slides[0].img;
    }
    return slides[currSlide + 1].img;
  };
  return (
    <div className="app">
      <div className="cardList">
        <button className="cardList__btn btn btn--left" onClick={nextSlide}>
          <div className="icon">⇽</div>
        </button>
        <div className="cards__wrapper">
          <div className="card current--card">
            <div className="card__image">
              <img src={slides[currSlide].img}></img>
            </div>
          </div>
          <div className="card next--card">
            <div className="card__image">
              <img src={nextImage}></img>
            </div>
          </div>
          <div className="card previous--card">
            <div className="card__image">
              <img src={prevImage}></img>
            </div>
          </div>
        </div>
        <button className="cardList__btn btn btn--right" onClick={prevSlide}>
          <div className="icon">⇾</div>
        </button>
      </div>
      <div className="infoList">
        <div className="info__wrapper">
          <div className="info current--info">
            <h1 className="text name">Hello</h1>
            <h4 className="text location">world</h4>
            <p className="text description">Yes Yes</p>
          </div>
          <div className="info next--info">
            <h1 className="text name">Hello</h1>
            <h4 className="text location">world</h4>
            <p className="text description">Yes Yes</p>
          </div>
          <div className="info previous--info">
            <h1 className="text name">Hello</h1>
            <h4 className="text location">world</h4>
            <p className="text description">Yes Yes</p>
          </div>
        </div>
      </div>
      <div className="app__bg">
        <div className="app__bg__image current--image">
          <img src="1.jpeg"></img>
        </div>
        <div className="app__bg__image next--image">
          <img src="2.jpeg"></img>
        </div>
        <div className="app__bg__image previous--image">
          <img src="3.jpeg"></img>
        </div>
      </div>
    </div>
  );
};
export default Test;
