import React from "react";
import "./Header.css";
import logo from "../../Assets/logo.png";
import { gsap } from "gsap";
const Header = () => {
  const [currColor, setColor] = React.useState("");
  console.log(document.querySelector("li").style.backgroundColor);
  setColor(document.querySelector("li").style.backgroundColor);
  const onEnter = ({ currentTarget }) => {
    gsap.to(currentTarget, { backgroundColor: "#e77614" });
  };

  const onLeave = ({ currentTarget }) => {
    gsap.to(currentTarget, { backgroundColor: "#" + currColor.toString() });
  };
  return (
    <section class="navigation">
      <div class="nav-container">
        <div class="brand">
          <img src={logo}></img>
        </div>
        <nav>
          <ul class="nav-list">
            <li>
              <a href="#!" onMouseEnter={onEnter} onMouseLeave={onLeave}>
                Home
              </a>
            </li>
            <li>
              <a href="#!" onMouseEnter={onEnter} onMouseLeave={onLeave}>
                About
              </a>
            </li>
            <li></li>
            <li>
              <a href="#!" onMouseEnter={onEnter} onMouseLeave={onLeave}>
                Showcase
              </a>
            </li>
            <li>
              <a href="#!" onMouseEnter={onEnter} onMouseLeave={onLeave}>
                Contact
              </a>
            </li>
          </ul>
        </nav>
      </div>
    </section>
  );
};

export default Header;
