import React from "react";
import "./Card.css";
const Card = () => {
  const { useState, useRef, useEffect } = React;

  const useTilt = (active) => {
    const ref = useRef(null);

    useEffect(() => {
      if (!ref.current || !active) {
        return;
      }

      const state = {
        rect: undefined,
        mouseX: undefined,
        mouseY: undefined,
      };

      let el = ref.current;

      const handleMouseMove = (e) => {
        if (!el) {
          return;
        }
        if (!state.rect) {
          state.rect = el.getBoundingClientRect();
        }
        state.mouseX = e.clientX;
        state.mouseY = e.clientY;
        const px = (state.mouseX - state.rect.left) / state.rect.width;
        const py = (state.mouseY - state.rect.top) / state.rect.height;

        el.style.setProperty("--px", px);
        el.style.setProperty("--py", py);
      };

      el.addEventListener("mousemove", handleMouseMove);

      return () => {
        el.removeEventListener("mousemove", handleMouseMove);
      };
    }, [active]);

    return ref;
  };

  const Slide = ({
    image,
    title,
    subtitle,
    description,
    offset,
    isPageBackground,
  }) => {
    const active = offset === 0 ? true : null,
      ref = useTilt(active);

    return (
      <div
        ref={ref}
        className="slide"
        data-active={active}
        style={{
          "--offset": offset,
          "--dir": offset === 0 ? 0 : offset > 0 ? 1 : -1,
        }}
      >
        {isPageBackground && (
          <div
            className="slideBackground"
            style={{
              backgroundImage: `url('${image}')`,
            }}
          />
        )}
        <div
          className="slideContent"
          style={{
            backgroundImage: `url('${image}')`,
          }}
        >
          <div className="slideContentInner">
            {title && (
              <h2 className="slideTitle" dir="auto">
                {title}
              </h2>
            )}
            {subtitle && (
              <h3 className="slideSubtitle" dir="auto">
                {subtitle}
              </h3>
            )}
            {description && (
              <p className="slideDescription" dir="auto">
                {description}
              </p>
            )}
          </div>
        </div>
      </div>
    );
  };

  const Carousel = ({ slides, isPageBackground }) => {
    const [slideIndex, setSlideIndex] = useState(0);

    const handlePrevSlide = () => {
      setSlideIndex((prev) => (prev === 0 ? slides.length - 1 : prev - 1));
    };

    const handleNextSlide = () => {
      setSlideIndex((prev) => (prev + 1) % slides.length);
    };

    return (
      <section className="slidesWrapper">
        <div className="slides">
          <button className="prevSlideBtn" onClick={handlePrevSlide}>
            <i className="fas fa-chevron-left" />
          </button>

          {[...slides, ...slides, ...slides].map((slide, i) => {
            let offset = slides.length + (slideIndex - i);

            if (typeof slide === "string") {
              return (
                <Slide
                  image={slide}
                  offset={offset}
                  isPageBackground={isPageBackground}
                  key={i}
                />
              );
            } else {
              return (
                <Slide
                  image={slide.image}
                  title={slide.title}
                  subtitle={slide.subtitle}
                  description={slide.description}
                  offset={offset}
                  isPageBackground={isPageBackground}
                  key={i}
                />
              );
            }
          })}
          <button className="nextSlideBtn" onClick={handleNextSlide}>
            <i className="fas fa-chevron-right" />
          </button>
        </div>
      </section>
    );
  };
};
export default Card;

