import React from "react";
import "./ImageSlider.css";
import { motion, AnimatePresence } from "framer-motion";
import { wrap } from "popmotion";

const ImageSlider = ({ slides }) => {
  const [[page, direction], setPage] = React.useState([0, 0]);
  const imageIndex = wrap(0, slides.length, page);
  const paginate = (newDirection) => {
    setPage([page + newDirection, newDirection]);
  };
  return (
    <div>
      <AnimatePresence
        className="slideContainer"
        initial={false}
        custom={direction}
      >
        <motion.img
          inital={{ opacity: 0 }}
          key={page}
          src={slides[imageIndex].img}
          custom={direction}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{
            x: {
              type: "srping",
              stiffness: 300,
              damping: 300,
              opacity: { duration: 1 },
            },
          }}
          drag="x"
          dragConstraints={{ left: 0, right: 0 }}
          dragElastic={1}
        ></motion.img>
        <motion.div className="slideInfo">
          <h1 className="slideTitle">{slides[imageIndex].title}</h1>
          <p className="slideDescription">{slides[imageIndex].description}</p>
        </motion.div>
      </AnimatePresence>
      <motion.button
        whileHover={{ scale: 1.2, transition: { duration: 0.5 } }}
        whileTap={{ scale: 0.9 }}
        className="nextBtn"
        onClick={() => paginate(1)}
      >
        ≻
      </motion.button>
      <motion.button
        whileHover={{ scale: 1.2, transition: { duration: 0.5 } }}
        whileTap={{ scale: 0.9 }}
        className="prevBtn"
        onClick={() => paginate(-1)}
      >
        ≺
      </motion.button>
    </div>
  );
};
export default ImageSlider;
