import React from "react";
import "./ImageSlider.css";
import {
  Swiper,
  SwiperSlide,
  Navigation,
  Pagination,
  Scrollbar,
  A11y,
} from "swiper/react";
import "swiper/css";

const ImageSlider = ({ slides }) => {
  return (
    <Swiper
      // install Swiper modules
      modules={[Navigation, Pagination, Scrollbar, A11y]}
      spaceBetween={50}
      slidesPerView={3}
      navigation
      pagination={{ clickable: true }}
      scrollbar={{ draggable: true }}
      onSwiper={(swiper) => console.log(swiper)}
      onSlideChange={() => console.log("slide change")}
    >
      <SwiperSlide>
        <img src={slides[0].img}></img>
      </SwiperSlide>
      <SwiperSlide>
        <img src={slides[1].img}></img>
      </SwiperSlide>
      <SwiperSlide>
        <img src={slides[2].img}></img>
      </SwiperSlide>
      <SwiperSlide>
        <img src={slides[0].img}></img>
      </SwiperSlide>
    </Swiper>
  );
};
export default ImageSlider;
