import React, { useEffect } from "react";
import "./Showcase.css";
import { Slides } from "./Slides";
import Card from "./Card";
import Particles from "react-tsparticles";

const Showcase = () => {
  const slides = Slides;
  const [index, setIndex] = React.useState(0);
  const slidesLength = slides.length;
  const nextHandler = () => {
    if (index < slides.length - 1) {
      setIndex(index + 1);
    } else {
      setIndex(0);
    }
  };

  const prevHandler = () => {
    if (index > 0) {
      setIndex(index - 1);
    } else {
      setIndex(slides.length - 1);
    }
  };
  return (
    <div className="showcaseContainer">
      <Particles
        canvasClassName="particle-canvas"
        options={{
          fpsLimit: 60,
          emitters: {
            direction: "bottom",
          },
          particles: {
            number: {
              value: 0,
            },
            life: {
              count: 5,
            },

            stroke: {
              color: {
                value: "#2BFFE6",
              },
              width: 1,
            },
            rotate: {
              path: true,
              value: 180,
            },
            move: {
              straight: true,
              enable: true,
              speed: { min: 10, max: 20 },
              outModes: {
                default: "destroy",
                top: "none",
              },
              trail: {
                fillColor: "#000",
                enable: true,
                length: 3,
              },
            },
          },
        }}
      />
      <button className="nextSlide" onClick={nextHandler}>
        ≻
      </button>
      <button className="prevSlide" onClick={prevHandler}>
        ≺
      </button>
      <div className="slidesContainer">
        {index - 1 > 0 ? (
          <Card slide={slides[index - 1]} className="leftSlide" />
        ) : (
          <Card slide={slides[slidesLength - 1]} className="leftSlide" />
        )}

        <Card slide={slides[index]} className="centerSlide" />

        {index + 1 < slidesLength ? (
          <Card slide={slides[index + 1]} className="leftSlide" />
        ) : (
          <Card slide={slides[0]} className="leftSlide" />
        )}
      </div>
    </div>
  );
};
export default Showcase;
