import React from "react";
import "./Showcase.css";
import ImageSlider from "../ImageSlider/ImageSlider";
import { Slides } from "../ImageSlider/Slides";
import { motion } from "framer-motion";

const Showcase = () => {
  const slides = Slides;
  const [currSlide, setSlide] = React.useState(0);
  const nextSlide = () => {
    if (currSlide + 1 < slides.length) {
      setSlide(currSlide + 1);
    } else {
      setSlide(0);
    }
  };
  const prevSlide = () => {
    if (currSlide != 0) {
      setSlide(currSlide - 1);
    } else {
      setSlide(0);
    }
  };
  return (
    <div>
      <div className="showcaseContainer">
        <button className="leftArrow" onClick={prevSlide}>
          ≺
        </button>
        <button className="rightArrow" onClick={nextSlide}>
          ≻
        </button>
        <motion.div
          initial={{ opacity: 0, x: -1000 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{
            ease: "easeInOut",
            delay: 0,
          }}
          className="slideContainer"
        >
          <ImageSlider slides={slides[currSlide]} />
        </motion.div>
      </div>
    </div>
  );
};
export default Showcase;
