import React from "react";
import "./PsudoAlgo.css";
import { AlgoList } from "./PsudoAlgoData";
export const PsudoAlgo = () => {
  const [option, setOption] = React.useState("Merge Sort");
  const selectHandler = (e) => {
    setOption(e.target.value);
  };
  const data = AlgoList.filter((record) => record.title == option);
  return (
    <>
      <li className="algoOption">
        <select onChange={selectHandler}>
          <option className="bubbleSort">Merge Sort</option>
          <option className="quickSort">Quick Sort</option>
          <option className="shellSort">ShellSort</option>
          <option className="heapSort">Heap Sort</option>
          <option className="mergeSort">Bubble Sort</option>
          <option className="emptySort">Empty Sort</option>
          <option className="selectionSort">Selection Sort</option>
          <option className="insertionSort">Insertion Sort</option>
        </select>
      </li>
      <div className="title">
        <h1>{data[0].title}</h1>
      </div>
      <div className="psudo">
        <li>
          Description:
          <p>{data[0].description}</p>
        </li>
        <p>
          Algorithm:
          <li>
            {data[0].steps.map((step, index) => {
              console.log(step, index);
            })}
          </li>
        </p>
      </div>
    </>
  );
};
