import React from "react";
import "./ImageSlider.css";

const ImageSlider = ({ slides }) => {
  const [currentSlide, setSlide] = React.useState(0);
  const [prevSlide, setPrevSlide] = React.useState(slides.length - 1);
  const [nextSlide, setNextSlide] = React.useState(currentSlide + 1);

  const prevSlideHandler = () => {
    if (currentSlide != 1) {
      setSlide(currentSlide - 1);
      setPrevSlide(currentSlide - 2);
    } else {
      setSlide(0);
      setPrevSlide(slides.length - 1);
    }
  };
  const nextSlideHandler = () => {
    if (currentSlide != slides.length - 2) {
      setSlide(currentSlide + 1);
      setNextSlide(currentSlide + 2);
    } else {
      setSlide(currentSlide + 1);
      setPrevSlide(slides.length - 1);
    }
  };

  return (
    <div className="slideWrapper">
      <button className="leftBtn" onClick={prevSlideHandler}>
        ≺
      </button>
      <div className="leftSlide">
        <img className="prevSlide" src={slides[prevSlide].img}></img>
        <div className="slideInfo">
          <h1>{slides[prevSlide].title}</h1>
          <p className="slideDescription">{slides[prevSlide].description}</p>
        </div>
      </div>
      <div className="mainSlide">
        <img className="currSlide" src={slides[currentSlide].img}></img>
        <div className="slideInfo">
          <h1>{slides[currentSlide].title}</h1>
          <p className="slideDescription">{slides[currentSlide].description}</p>
        </div>
      </div>
      <div className="rightSlide">
        <img className="nextSlide" src={slides[nextSlide].img}></img>
        <div className="slideInfo">
          <h1>{slides[nextSlide].title}</h1>
          <p className="slideDescription">{slides[nextSlide].description}</p>
        </div>
      </div>
      <button className="rightBtn" onClick={nextSlideHandler}>
        ≻
      </button>
    </div>
  );
};
export default ImageSlider;
