import React from "react";
import "./Card.css";

const Card = ({ slide, className, state }) => {
  return (
    <div className="slideContent">
      {className == "centerSlide" ? (
        <div className="slideTitle">
          <h1>{slide.title}</h1>
        </div>
      ) : null}
      <img
        className={className}
        src={slide.image}
        onAnimationEnd={() => (state = 0)}
        slideFromRight={state}
      ></img>
      {className == "centerSlide" ? (
        <div className="slideCaption">{slide.caption}</div>
      ) : null}
    </div>
  );
};
export default Card;
