import React from "react";
import "./SortingVisualizer.css";

export default class SortingVisualizer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      array: [],
    };
  }
  //When Component First Loads or we call it, Resets the Array or Generates a new array.
  componentDidMount() {
    this.resetArray();
  }

  resetArray() {
    const array = [];
    for (let i = 0; i < 33; i++) {
      array.push(randomIntFromInterval(5, 100));
    }
    this.setState({ array });
  }

  render() {
    const { array } = this.state;

    return (
      <div className="arrayContainer">
        {array.map((value, idx) => (
          <div
            className="arrayBar"
            key={idx}
            style={{ height: `${value * 5}px` }}
          >
            {value}
          </div>
        ))}
        <ul className="navLinks">
          <li>
            <select>
              <option className="bubbleSort">Bubble Sort</option>
              <option className="quickSort">Quick Sort</option>
              <option className="shellSort">ShellSort</option>
              <option className="heapSort">Heap Sort</option>
              <option className="mergeSort">Merge Sort</option>
              <option className="emptySort">Empty Sort</option>
              <option className="selectionSort">Selection Sort</option>
              <option className="insertionSort">Insertion Sort</option>
            </select>
          </li>
          <li>
            <button onClick={this.resetArray}>Generate New Array.</button>
          </li>
          <li>
            <button>Sort</button>
          </li>
        </ul>
      </div>
    );
  }
}
//Generates random number between min and max.
function randomIntFromInterval(min, max) {
  return Math.floor(Math.random() * (max - min + 1) + min);
}
