import "./Showcase.css";
import { useState } from "react";
import Slider from "react-slick";
import { Slides } from "./Slides";
function Showcase() {
  const slides = Slides;

  const [imageIndex, setImageIndex] = useState(0);
  const nextHandler = () => {
    if (index < slides.length - 1) {
      setIndex(index + 1);
    } else {
      setIndex(0);
    }
  };
  const prevHandler = () => {
    if (index > 0) {
      setIndex(index - 1);
    } else {
      setIndex(slides.length - 1);
    }
  };
  const settings = {
    infinite: true,
    lazyLoad: true,
    speed: 300,
    slidesToShow: 3,
    centerMode: true,
    centerPadding: 0,
    nextArrow: nextHandler,
    prevArrow: prevHandler,
    beforeChange: (current, next) => setImageIndex(next),
  };

  return (
    <div className="showcaseContainer">
      <div className="arrow next" onClick={nextHandler}>
        ≫
      </div>
      <div className="arrow prev" onClick={prevHandler}>
        ≪
      </div>
      <Slider {...settings}>
        {slides.map((img, idx) => (
          <div className={idx === imageIndex ? "slide activeSlide" : "slide"}>
            <img src={img.img} alt={img} />
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default Showcase;
