import React from "react";
import "./ImageSlider.css";

const ImageSlider = ({ slides }) => {
  const [currentSlide, setSlide] = React.useState(0);
  const previousSlide = () => {
    if (currentSlide - 1 < 0) {
      return false;
    }
    return true;
  };
  const nextSlide = () => {
    if (currentSlide + 1 > slides.length) {
      return false;
    }
    return true;
  };

  const nextSlideHandler = () => {
    if (nextSlide) {
      setSlide(currentSlide + 1);
    } else {
      setSlide(0);
    }
  };
  const prevSlideHandler = () => {
    if (previousSlide) {
      setSlide(currentSlide - 1);
    } else {
      setSlide(0);
    }
  };
  return (
    <div className="slideWrapper">
      <button className="leftBtn">≺</button>
      <div className="leftSlide">
        <img
          className="prevSlide"
          src={
            previousSlide()
              ? slides[currentSlide - 1].img
              : slides[slides.length - 1].img
          }
        ></img>
        <div className="slideInfo">
          <h1>
            {previousSlide()
              ? slides[currentSlide - 1].title
              : slides[slides.length - 1].title}
          </h1>
          <p className="slideDescription">
            {previousSlide()
              ? slides[currentSlide - 1].description
              : slides[slides.length - 1].description}
          </p>
        </div>
      </div>
      <div className="mainSlide">
        <img className="currSlide" src={slides[currentSlide].img}></img>
        <div className="slideInfo">
          <h1>{slides[currentSlide].title}</h1>
          <p className="slideDescription">{slides[currentSlide].description}</p>
        </div>
      </div>
      <div className="rightSlide">
        <img
          className="nextSlide"
          src={nextSlide() ? slides[currentSlide + 1].img : slides[0].img}
        ></img>
        <div className="slideInfo">
          <h1>
            {nextSlide() ? slides[currentSlide + 1].title : slides[0].title}
          </h1>
          <p className="slideDescription">
            {nextSlide()
              ? slides[currentSlide + 1].description
              : slides[0].description}
          </p>
        </div>
      </div>
      <button className="rightBtn">≻</button>
    </div>
  );
};
export default ImageSlider;
