import React from "react";
import "./Header.css";
import logo from "../../Assets/logo.png";
const Header = () => {
  // fixed Header
  window.addEventListener("scroll", function () {
    const header = document.querySelector(".header");
    header.classList.toggle("active", window.scrollY > 100);
  });
  const onEnter = ({ currentTarget }) => {
    gsap.to(currentTarget, { backgroundColor: "#e77614" });
  };

  const onLeave = ({ currentTarget }) => {
    gsap.to(currentTarget, { backgroundColor: "#28a92b" });
  };
  // Toogle Menu
  const [Mobile, setMobile] = React.useState(false);
  return (
    <>
      <header className="header">
        <div className="logo">
          <img src={logo}></img>
        </div>
        <ul className="navLinks">
          <li>
            <a href="/home" onEnter={onEnter} onLeave={onLeave}>
              Home
            </a>
          </li>
          <li>
            <a href="/about" onEnter={onEnter} onLeave={onLeave}>
              About
            </a>
          </li>
          <li>
            <a href="/showcase" onEnter={onEnter} onLeave={onLeave}>
              Showcase
            </a>
          </li>
          <li>
            <a href="/mockup" onEnter={onEnter} onLeave={onLeave}>
              Mockup
            </a>
          </li>
        </ul>
      </header>
    </>
  );
};

export default Header;
