import React from "react";
import "./SortingVisualizer.css";
import {
  doMerge,
  getMergeSortAnimations,
  getNewArray,
} from "../SortingAlgorithms/SortingAlgorithms";

const ANIMATION_SPEED_MS = 1;

// Change this value for the number of bars (value) in the array.
const NUMBER_OF_ARRAY_BARS = 310;

// This is the main color of the array bars.
const PRIMARY_COLOR = "blue";

// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = "red";

export default class SortingVisualizer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      array: [],
    };
  }
  //When Component First Loads or we call it, Resets the Array or Generates a new array.
  componentDidMount() {
    this.resetArray();
  }

  resetArray() {
    const array = [];
    for (let i = 0; i < 33; i++) {
      array.push(randomIntFromInterval(5, 100));
    }
    this.setState({ array });
  }
  quickSort() {
    const animations = getMergeSortAnimations(this.state.array);
    for (let i = 0; i < animations.length; i++) {
      const arrayBars = document.getElementsByClassName("arrayBar");
      const isColorChange = i % 3 !== 2;
      if (isColorChange) {
        const [barOneIdx, barTwoIdx] = animations[i];
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;
        const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
        setTimeout(() => {
          barOneStyle.backgroundColor = color;
          barTwoStyle.backgroundColor = color;
        }, i * ANIMATION_SPEED_MS);
      } else {
        setTimeout(() => {
          const [barOneIdx, newHeight] = animations[i];
          const barOneStyle = arrayBars[barOneIdx].style;
          barOneStyle.height = `${newHeight}px`;
        }, i * ANIMATION_SPEED_MS);
      }
    }
  }
  newArray() {
    this.state.array = getNewArray(this.state.array);
  }
  render() {
    const { array } = this.state;

    return (
      <div className="container">
        <ul className="navLinks">
          <li>
            <select>
              <option className="bubbleSort">Bubble Sort</option>
              <option className="quickSort">Quick Sort</option>
              <option className="shellSort">ShellSort</option>
              <option className="heapSort">Heap Sort</option>
              <option className="mergeSort">Merge Sort</option>
              <option className="emptySort">Empty Sort</option>
              <option className="selectionSort">Selection Sort</option>
              <option className="insertionSort">Insertion Sort</option>
            </select>
          </li>
          <li>
            <button onClick={() => this.resetArray()}>
              Generate New Array.
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                this.quickSort();
                this.state.array = this.newArray();
                console.log(this.stte.array);
              }}
            >
              Sort
            </button>
          </li>
        </ul>
        <div className="arrayContainer">
          {array.map((value, idx) => (
            <div
              className="arrayBar"
              key={idx}
              style={{ height: `${value * 5}px` }}
            >
              {value}
            </div>
          ))}
        </div>
      </div>
    );
  }
}
//Generates random number between min and max.
function randomIntFromInterval(min, max) {
  return Math.floor(Math.random() * (max - min + 1) + min);
}
